import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from '../services/api.service';
import {HttpClient} from '@angular/common/http';

@Component({
  selector: 'app-search',
  templateUrl: './search.component.html',
  styleUrls: ['./search.component.scss']
})

export class SearchComponent implements OnInit {
  searchQuery: string = '';
  showZeroStateMessage: boolean = false;

  constructor(private router: Router, private apiService: ApiService, private http: HttpClient) { }

  ngOnInit(): void {
  }

  searchUser(): void {
    if (this.searchQuery.trim() !== '') {
      this.apiService.getUser(this.searchQuery).subscribe(
        (response: any) => {
          // User found, navigate to the result page with username as parameter
          this.router.navigate(['/result', { username: this.searchQuery }]);
        },
        (error: any) => {
          // User not found, stay on the search page
          this.router.navigate(['/search']);
          this.showZeroStateMessage = true;
        }
      );
    }
  }
}
<body class="flex flex-col h-screen w-screen items-center justify-center bg-gray-400">
  <div class="flex flex-col items-center">
    <h1 class="text-4xl font-bold mb-4 text-center">Enter GitHub username</h1>
    <div class="flex flex-col items-center sm:flex-row sm:items-stretch sm:w-full max-w-md">
      <input type="text" [(ngModel)]="searchQuery" class="w-full px-4 py-2 border border-gray-300 hover:bg-gray-200 rounded-md outline-none focus:border-blue-500 mb-4 sm:mb-0 sm:mr-2" >
      <button class="w-full px-4 py-2 font-bold bg-blue-400 text-white rounded-md hover:bg-blue-500 sm:w-auto" (click)="searchUser()">Search</button>
    </div>

    <div *ngIf="showZeroStateMessage" class="text-gray-600 mt-4">User not found</div>
  </div>
</body>
